(44, 165, 'Thank you', 'thank-you', '<div class="row justify-content-center text-center mb-5">\n\n<div class="col-12 col-md-9">\n\n<div class="alert alert-primary">\n\n<h2 class="mb-3">Your offer has been submitted successfully.</h2>\n\n<p class="lead">Thank you for joining our <strong>COVID</strong> Support campaign for the Joomla community.</p>\n\n<p class="lead">We got your offer submission, our team will review your offer and if it is a valid offer, we will publish and list your offer in the campaign offer list main page</p>\n\n<p class="lead">You should get a notification email when your offer is published.</p>\n\n</div>\n\n</div>\n\n</div>', '', 1, 10, '2021-01-21 06:48:31', 42, '', '2021-01-21 06:48:31', 42, 0, '0000-00-00 00:00:00', '2021-01-21 06:48:31', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 15, 0, '', '', 1, 39, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '', ''),
